import "./styles.css";
import PieChart from "./components/PieChart";
import StyledTable from "./components/Table";
import Buttons from "./components/Buttons";
import React, { useEffect } from "react";
import axios from "axios";

const initialState = {
  count: 0,
  currentDate: new Date(),
  personInfo: [],
};

const reducer = (state, action) => {
  console.log("reducer", state, action);
  switch (action.type) {
    case "INCREMENT":
      return { ...state, count: state.count + 1 };
    case "GET_PERSON_INFO":
      return { ...state, personInfo: [...action.payload] };
    case "GET_INITIAL_DATE":
      return { ...state, currentDate: action.payload };
    default:
      return state;
  }
};

export default function App() {
  const [state, dispatch] = React.useReducer(reducer, initialState);
  console.log("state", state);

  const getData = () => {
    console.log("get data");
    fetch("http://127.0.0.1:5500/public/data/vaccine_dates.json")
      .then((response) => response.json())
      .then((myJson) => {
        // console.log(myJson, "myJson");
        dispatch({ type: "GET_PERSON_INFO", payload: myJson });
      });
  };

  const getInitialDate = () => {
    fetch("http://127.0.0.1:5500/public/data/current_date.json")
      .then((response) => response.json())
      .then((res) => {
        // console.log(res, "myJson");
        dispatch({ type: "GET_INITIAL_DATE", payload: res.current_date });
      });
  };

  const endpoints = [
    "http://127.0.0.1:5500/public/data/current_date.json",
    "http://127.0.0.1:5500/public/data/vaccine_dates.json",
  ];

  const getAllData = () => {
    Promise.all(endpoints.map((endpoint) => axios.get(endpoint))).then(
      ([ res1, res2

      ]) => {
        console.log(res1);
      }
    );
  };

  useEffect(() => {
    getAllData();
  }, []);

  return (
    <div className='App'>
      <h1>count:{state.count}</h1>
      <div className='chart'>
        <PieChart data={[60, 40]} />
      </div>
      <div className='buttons'>
        <Buttons handleIncrease={() => dispatch({ type: "INCREMENT" })} />
      </div>
      <b style={{ textAlign: "center" }}>
        X out of Y persons have been vaccinated.
      </b>
      <div className='table'>
        <StyledTable />
      </div>
    </div>
  );
}
